import React, { useEffect, useState } from 'react';
import API from '../api';
import AuthModal from '../parts/AuthModal';
import './Home.css';

export default function Home({ token, onLogin }) {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showAuth, setShowAuth] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [search, setSearch] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const categories = [
    'All',
    'Rice',
    'Roti',
    'Pizza',
    'Burger',
    'Cold Drink',
    'Dessert',
    'Snacks',
  ];

  useEffect(() => {
    loadMenu();
    if (localStorage.getItem('token')) loadCart();
  }, []);

  const loadMenu = async () => {
    const data = await API('/items');
    setItems(data || []);
    setFilteredItems(data || []);
  };

  const loadCart = async () => {
    const res = await API('/cart');
    if (res.items) {
      setCart(res.items);
      setShowCart(res.items.length > 0);
    }
  };

  const addToCart = async (item) => {
    if (!localStorage.getItem('token')) {
      setShowAuth(true);
      return;
    }
    await API('/cart/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ item_id: item.id, quantity: 1 }),
    });
    loadCart();
  };

  const increaseQty = async (item) => {
    await API('/cart/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ item_id: item.id, quantity: 1 }),
    });
    loadCart();
  };

  const decreaseQty = async (item) => {
    if (item.quantity === 1) {
      await API('/cart/remove', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item_id: item.id }),
      });
    } else {
      await API('/cart/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item_id: item.id, quantity: -1 }),
      });
    }
    loadCart();
  };

  const checkout = async () => {
    await API('/orders/checkout', { method: 'POST' });
    setCart([]);
    setShowCart(false);
  };

  // Filter items by search & category
  useEffect(() => {
    let list = [...items];
    if (selectedCategory !== 'All') {
      list = list.filter((i) => i.category === selectedCategory);
    }
    if (search.trim()) {
      list = list.filter((i) =>
        i.name.toLowerCase().includes(search.toLowerCase())
      );
    }
    setFilteredItems(list);
  }, [search, selectedCategory, items]);

  const total = cart.reduce((t, i) => t + i.price * i.quantity, 0);

  return (
    <div className={`split-layout ${showCart ? '' : 'full-width'}`}>
      {/* LEFT: MENU */}
      <div className="menu-section">
        <h2>Menu</h2>

        {/* üîç Search Bar */}
        <input
          type="text"
          placeholder="Search for dishes..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-bar"
        />

        {/* üçΩ Category Tabs */}
        <div className="categories">
          {categories.map((cat) => (
            <button
              key={cat}
              className={`cat-btn ${
                selectedCategory === cat ? 'active' : ''
              }`}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* üç≤ Menu Grid */}
        <div className="grid">
          {filteredItems.map((it) => (
            <div key={it.id} className="card">
              <h4>{it.name}</h4>
              <p>{it.description}</p>
              <div>‚Çπ{it.price}</div>
              <button className="button" onClick={() => addToCart(it)}>
                Add to Cart
              </button>
            </div>
          ))}
          {filteredItems.length === 0 && <p>No items found.</p>}
        </div>
      </div>

      {/* RIGHT: CART */}
      {showCart && (
        <div className="cart-section">
          <h2>Your Cart</h2>
          {cart.length ? (
            <>
              {cart.map((it) => (
                <div key={it.id} className="cart-item">
                  <div>
                    <b>{it.name}</b>
                  </div>
                  <div className="cart-actions">
                    <button onClick={() => decreaseQty(it)}>-</button>
                    <span>{it.quantity}</span>
                    <button onClick={() => increaseQty(it)}>+</button>
                  </div>
                  <div className="cart-price">‚Çπ{it.price * it.quantity}</div>
                </div>
              ))}
              <div className="cart-total">
                <h3>Total: ‚Çπ{total}</h3>
                <button className="button" onClick={checkout}>
                  Checkout
                </button>
              </div>
            </>
          ) : (
            <p>Your cart is empty.</p>
          )}
        </div>
      )}

      {showAuth && (
        <AuthModal onClose={() => setShowAuth(false)} onLogin={onLogin} />
      )}
    </div>
  );
}
