import React, { useEffect, useState } from 'react';
import API from '../api';
import AuthModal from '../parts/AuthModal';
import './Home.css';

export default function Home({ token, onLogin }) {
  const [items, setItems] = useState([]);
  const [showAuth, setShowAuth] = useState(false);
  const [cart, setCart] = useState([]);

  // Fetch menu and cart
  useEffect(() => {
    loadMenu();
    if (localStorage.getItem('token')) loadCart();
  }, []);

  const loadMenu = async () => {
    const data = await API('/items');
    setItems(data || []);
  };

  const loadCart = async () => {
    const res = await API('/cart');
    if (res.items) setCart(res.items);
  };

  // Add item to cart
  const addToCart = async (item) => {
    if (!localStorage.getItem('token')) {
      setShowAuth(true);
      return;
    }
    await API('/cart/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ item_id: item.id, quantity: 1 }),
    });
    loadCart();
  };

  // Increase item quantity
  const increaseQty = async (item) => {
    await API('/cart/add', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ item_id: item.id, quantity: 1 }),
    });
    loadCart();
  };

  // Decrease item quantity
  const decreaseQty = async (item) => {
    if (item.quantity === 1) {
      await API('/cart/remove', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item_id: item.id }),
      });
    } else {
      await API('/cart/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item_id: item.id, quantity: -1 }),
      });
    }
    loadCart();
  };

  const total = cart.reduce((t, i) => t + i.price * i.quantity, 0);

  return (
    <div className="split-layout">
      {/* LEFT: Menu */}
      <div className="menu-section">
        <h2>Menu</h2>
        <div className="grid">
          {items.map((it) => (
            <div key={it.id} className="card">
              <h4>{it.name}</h4>
              <p style={{ minHeight: 40 }}>{it.description}</p>
              <div>₹{it.price}</div>
              <button className="button" onClick={() => addToCart(it)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* RIGHT: Cart */}
      <div className="cart-section">
        <h2>Your Cart</h2>
        {cart.length ? (
          <>
            {cart.map((it) => (
              <div key={it.id} className="cart-item">
                <div>
                  <b>{it.name}</b>
                </div>
                <div className="cart-actions">
                  <button onClick={() => decreaseQty(it)}>-</button>
                  <span>{it.quantity}</span>
                  <button onClick={() => increaseQty(it)}>+</button>
                </div>
                <div className="cart-price">₹{it.price * it.quantity}</div>
              </div>
            ))}
            <div className="cart-total">
              <h3>Total: ₹{total}</h3>
              <button
                className="button"
                onClick={async () => {
                  await API('/orders/checkout', { method: 'POST' });
                  loadCart();
                }}
              >
                Checkout
              </button>
            </div>
          </>
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      {/* Auth Modal */}
      {showAuth && (
        <AuthModal onClose={() => setShowAuth(false)} onLogin={onLogin} />
      )}
    </div>
  );
}
