// export default App;
import React, { useState } from "react";
import Home from "./pages/Home";
import Cart from "./pages/Cart";
import AuthModal from "./parts/AuthModal";
import Profile from "./pages/Profile";
import ZwigatoPopup from "./components/ZwigatoPopup";
import "./index.css";

function App() {
  const [page, setPage] = useState("home");
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [user, setUser] = useState(
    JSON.parse(localStorage.getItem("user") || "null")
  );
  const [showAuth, setShowAuth] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  // üîπ Handle Login Success
  const onLogin = (token, user) => {
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(user));
    setToken(token);
    setUser(user);
    setShowAuth(false);
  };

  // üîπ Handle Logout
  const onLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setToken(null);
    setUser(null);
    setDropdownOpen(false);
    setPage("home");
  };

  // üîπ Handle Cart Access
  const handleCartClick = () => {
    if (!user) {
      setShowPopup(true);
      return;
    }
    setPage("cart");
  };

  // üîπ Toggle Dropdown Safely
  const toggleDropdown = () => {
    setDropdownOpen((prev) => !prev);
  };

  return (
    <div>
      {/* üåø NAVBAR */}
      <nav className="navbar white-navbar">
        {/* Logo Section */}
        <div className="logo" onClick={() => setPage("home")}>
          <span className="logo-icon">üçΩÔ∏è</span>
          <span className="logo-text green-text">ZWIGATO</span>
          <span className="logo-sub">FAST & FRESH</span>
        </div>

        {/* Right-side Buttons */}
        <div className="nav-links">
          <button className="nav-btn cart-btn" onClick={handleCartClick}>
            üõí Cart
          </button>

          {!user && (
            <button
              className="nav-btn login-btn"
              onClick={() => setShowAuth(true)}
            >
              üîê Login
            </button>
          )}

          {user && (
            <div className="profile-wrapper">
              <img
                src={
                  user?.photo
                    ? `${process.env.REACT_APP_API_URL?.replace(
                        "/api",
                        ""
                      )}${user.photo}`
                    : "https://cdn-icons-png.flaticon.com/512/847/847969.png"
                }
                alt="profile"
                className="profile-pic"
                onClick={toggleDropdown}
              />

              {dropdownOpen && (
                <div className="profile-dropdown show">
                  <p>Hello, {user.name?.split(" ")[0]}</p>
                  <button
                    onClick={() => {
                      setPage("profile");
                      setDropdownOpen(false);
                    }}
                  >
                    View Profile
                  </button>
                  <button onClick={onLogout}>Logout</button>
                </div>
              )}
            </div>
          )}
        </div>
      </nav>

      {/* üçï Funny Popup */}
      <ZwigatoPopup
        show={showPopup}
        title="üçï Whoa there, foodie!"
        message="You need to log in before filling your cart! Hungry people must sign in first üòã"
        onClose={() => setShowPopup(false)}
        onConfirm={() => {
          setShowPopup(false);
          setShowAuth(true);
        }}
      />

      {/* üè† Page Content */}
      <div className="container">
        {page === "home" && <Home token={token} onLogin={onLogin} />}
        {page === "cart" && user && <Cart token={token} onLogin={onLogin} />}
        {page === "profile" && <Profile />}
      </div>

      {/* üîê Auth Modal */}
      {showAuth && (
        <AuthModal onClose={() => setShowAuth(false)} onLogin={onLogin} />
      )}

      {/* üåø FOOTER */}
      <footer className="footer">
        <div className="footer-container">
          <div className="footer-col">
            <h3>ZWIGATO</h3>
            <p>
              Order delicious meals from your favorite restaurants, delivered
              fast and fresh right to your doorstep. Explore a wide range of
              cuisines and enjoy the taste of happiness!
            </p>
          </div>

          <div className="footer-col">
            <h4>Quick Links</h4>
            <ul>
              <li onClick={() => setPage("home")}>Home</li>
              <li onClick={() => setPage("cart")}>Cart</li>
              <li onClick={() => setPage("profile")}>Profile</li>
            </ul>
          </div>

          <div className="footer-col">
            <h4>Legal</h4>
            <ul>
              <li>
                <a href="#">Terms & Conditions</a>
              </li>
              <li>
                <a href="#">Privacy Policy</a>
              </li>
              <li>
                <a href="#">Refund Policy</a>
              </li>
            </ul>
          </div>

          <div className="footer-col">
            <h4>Contact</h4>
            <p>Email: support@zwigato.com</p>
            <p>Phone: +91 98765 43210</p>

            <div className="social-links">
              <a href="#">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#">
                <i className="fab fa-instagram"></i>
              </a>
              <a href="#">
                <i className="fab fa-twitter"></i>
              </a>
            </div>

            <p className="developer">
              Developed by{" "}
              <a
                href="https://github.com/neelkanth"
                target="_blank"
                rel="noreferrer"
              >
                Neelkanth
              </a>
            </p>
          </div>
        </div>

        <div className="footer-bottom">
          ¬© 2025 ZWIGATO ‚Äî Fast & Fresh. All rights reserved.
        </div>
      </footer>
    </div>
  );
}

export default App;
