import React, { useEffect, useState } from "react";
import API from "../api";
import AuthModal from "../parts/AuthModal";
import "./Home.css";

export default function Home({ token, onLogin }) {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showAuth, setShowAuth] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  const categories = [
    "All",
    "Rice",
    "Roti",
    "Pizza",
    "Burger",
    "Cold Drink",
    "Dessert",
    "Snacks",
  ];

  // ‚úÖ Load menu and cart
  useEffect(() => {
    loadMenu();
    if (localStorage.getItem("token")) loadCart();
  }, []);

  // ‚úÖ Load menu items
  const loadMenu = async () => {
    try {
      const data = await API("/items");
      setItems(data || []);
      setFilteredItems(data || []);
    } catch (err) {
      console.error("‚ùå Failed to load menu:", err);
    }
  };

  // ‚úÖ Load user's cart
  const loadCart = async () => {
    try {
      const res = await API("/cart");
      const list = res.items || res.cart || [];
      const formatted = list.map((it) => ({
        cart_id: it.cart_id || it.id,
        item_id: it.item_id || it.id,
        name: it.name,
        price: Number(it.price),
        quantity: Number(it.quantity),
      }));
      setCart(formatted);
      setShowCart(formatted.length > 0);
    } catch (err) {
      console.error("‚ùå Failed to load cart:", err);
    }
  };

  // ‚úÖ Add to cart
  const addToCart = async (item) => {
    if (!localStorage.getItem("token")) {
      setShowAuth(true);
      return;
    }
    try {
      await API("/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: item.id, quantity: 1 }),
      });
      await loadCart();
    } catch (err) {
      console.error("‚ùå Add to cart failed:", err);
    }
  };

  // ‚úÖ Increase item quantity
  const increaseQty = async (itemId) => {
    try {
      await API("/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: itemId, quantity: 1 }),
      });
      await loadCart();
    } catch (err) {
      console.error("‚ùå Increase quantity failed:", err);
    }
  };

  // ‚úÖ Decrease item quantity or remove if 0
  const decreaseQty = async (item) => {
    try {
      if (item.quantity <= 1) {
        await API("/cart/remove", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ item_id: item.item_id }),
        });
      } else {
        await API("/cart/add", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ item_id: item.item_id, quantity: -1 }),
        });
      }
      await loadCart();
    } catch (err) {
      console.error("‚ùå Decrease quantity failed:", err);
    }
  };

  // ‚úÖ Checkout
  const checkout = async () => {
    try {
      await API("/cart/checkout", { method: "POST" });
      alert("‚úÖ Order placed successfully!");
      setCart([]);
      setShowCart(false);
    } catch (err) {
      console.error("‚ùå Checkout failed:", err);
    }
  };

  // ‚úÖ Search + filter
  useEffect(() => {
    let list = [...items];
    if (selectedCategory !== "All") {
      list = list.filter((i) => i.category === selectedCategory);
    }
    if (search.trim()) {
      list = list.filter((i) =>
        i.name.toLowerCase().includes(search.toLowerCase())
      );
    }
    setFilteredItems(list);
  }, [search, selectedCategory, items]);

  // ‚úÖ Calculate total
  const total = cart.reduce(
    (sum, i) => sum + i.price * i.quantity,
    0
  );

  return (
    <div className={`split-layout ${showCart ? "" : "full-width"}`}>
      {/* LEFT: MENU */}
      <div className="menu-section">
        <h2>Menu</h2>
        <input
          type="text"
          placeholder="Search for dishes..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-bar"
        />

        <div className="categories">
          {categories.map((cat) => (
            <button
              key={cat}
              className={`cat-btn ${
                selectedCategory === cat ? "active" : ""
              }`}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        <div className="grid">
          {filteredItems.map((it) => (
            <div key={it.id} className="card">
              <h4>{it.name}</h4>
              <p>{it.description}</p>
              <div>‚Çπ{it.price}</div>
              <button className="button" onClick={() => addToCart(it)}>
                Add to Cart
              </button>
            </div>
          ))}
          {filteredItems.length === 0 && <p>No items found.</p>}
        </div>
      </div>

      {/* RIGHT: CART */}
      {showCart && (
        <div className="cart-section">
          <h2>Your Cart</h2>
          {cart.length > 0 ? (
            <>
              <table className="cart-table">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Quantity</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((it) => (
                    <tr key={it.item_id}>
                      <td>{it.name}</td>
                      <td>
                        <div className="qty-controls">
                          <button
                            className="qty-btn"
                            onClick={() => decreaseQty(it)}
                          >
                            ‚àí
                          </button>
                          <span>{it.quantity}</span>
                          <button
                            className="qty-btn"
                            onClick={() => increaseQty(it.item_id)}
                          >
                            +
                          </button>
                        </div>
                      </td>
                      <td>‚Çπ{(it.price * it.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="cart-total">
                <h3>Total: ‚Çπ{total.toFixed(2)}</h3>
                <button className="button" onClick={checkout}>
                  Checkout
                </button>
              </div>
            </>
          ) : (
            <p>Your cart is empty.</p>
          )}
        </div>
      )}

      {showAuth && (
        <AuthModal onClose={() => setShowAuth(false)} onLogin={onLogin} />
      )}
    </div>
  );
}
