import React, { useState, useEffect } from "react";
import "./AuthModal.css";
console.log("ðŸ” API Base URL =", process.env.REACT_APP_API_URL);


function AuthModal({ onClose, onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    address: "",
    mobile: "",
    email: "",
    password: "",
    photo: null,
    photoPreview: null,
  });

  useEffect(() => {
    return () => {
      if (formData.photoPreview) {
        URL.revokeObjectURL(formData.photoPreview);
      }
    };
  }, [formData.photoPreview]);

  // const handleSubmit = async (e) => {
  //   e.preventDefault();

  //   const form = new FormData();
  //   Object.keys(formData).forEach((key) => {
  //     if (formData[key] && key !== "photoPreview") {
  //       form.append(key, formData[key]);
  //     }
  //   });

  //   const url = isLogin
  //     ? `${process.env.REACT_APP_API_URL}/users/login`
  //     : `${process.env.REACT_APP_API_URL}/users/signup`;

  //   try {
  //     const res = await fetch(url, {
  //       method: "POST",
  //       body: form,
  //     });

  //     const data = await res.json();

  //     if (data.token) {
  //       onLogin(data.token, data.user);
  //     } else {
  //       alert(data.message || "Something went wrong!");
  //     }
  //   } catch (err) {
  //     console.error("Fetch error:", err);
  //     alert("Failed to connect to backend. Please check your server.");
  //   }
  // };
  const handleSubmit = async (e) => {
  e.preventDefault();
  setError("");

  const { email, password } = form;

  if (!email || !password) {
    setError("Email and password are required.");
    return;
  }

  try {
    const endpoint = isLogin ? "/users/login" : "/users/signup";
    console.log("ðŸ“¡ Sending to:", endpoint, { email, password });

    const res = await API(endpoint, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }), // âœ… Must be stringified
    });

    console.log("âœ… Response:", res);

    if (res.token) {
      localStorage.setItem("token", res.token);
      onLogin(res.token);
      onClose();
    } else {
      setError(res.message || "Invalid server response.");
    }
  } catch (err) {
    console.error("âŒ Login failed:", err);
    setError(err.message || "Something went wrong.");
  }
};

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const previewUrl = URL.createObjectURL(file);
      setFormData({ ...formData, photo: file, photoPreview: previewUrl });
    }
  };

  return (
    <div className="auth-overlay">
      <div className="auth-modal animate-popup">
        <div className="auth-header">
          <h2>{isLogin ? "Welcome Back!" : "Create an Account"}</h2>
          <button className="close-btn" onClick={onClose}>
            âœ–
          </button>
        </div>

        <form className="auth-form" onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="photo-upload">
              <label htmlFor="photo">
                <div className="photo-frame">
                  {formData.photoPreview ? (
                    <img
                      src={formData.photoPreview}
                      alt="Preview"
                      className="photo-preview"
                    />
                  ) : (
                    <>
                      <span role="img" aria-label="camera" style={{ fontSize: "24px" }}>
                        ðŸ“·
                      </span>
                      <span>Upload</span>
                    </>
                  )}
                </div>
              </label>
              <input
                type="file"
                id="photo"
                accept="image/*"
                onChange={handleFileChange}
                hidden
              />
            </div>
          )}

          {!isLogin && (
            <>
              <input
                type="text"
                placeholder="Full Name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                required
              />
              <input
                type="number"
                placeholder="Age"
                value={formData.age}
                onChange={(e) => setFormData({ ...formData, age: e.target.value })}
                required
              />
              <input
                type="text"
                placeholder="Address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                required
              />
              <input
                type="text"
                placeholder="Mobile"
                value={formData.mobile}
                onChange={(e) => setFormData({ ...formData, mobile: e.target.value })}
                required
              />
            </>
          )}

          <input
            type="email"
            placeholder="Email"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={formData.password}
            onChange={(e) => setFormData({ ...formData, password: e.target.value })}
            required
          />

          <button type="submit" className="auth-btn">
            {isLogin ? "Login" : "Sign Up"}
          </button>
        </form>

        <div className="auth-footer">
          {isLogin ? (
            <p>
              New User? <span onClick={() => setIsLogin(false)}>Sign Up</span>
            </p>
          ) : (
            <p>
              Already have an account? <span onClick={() => setIsLogin(true)}>Login</span>
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
