import React, { useEffect, useState } from "react";
import API from "../api";
import AuthModal from "../parts/AuthModal";
import "./Home.css";

export default function Home({ token, onLogin }) {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [showAuth, setShowAuth] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [search, setSearch] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  const categories = [
    "All",
    "Rice",
    "Roti",
    "Pizza",
    "Burger",
    "Cold Drink",
    "Dessert",
    "Snacks",
  ];

  // üß© Load menu + cart on mount
  useEffect(() => {
    loadMenu();
    if (localStorage.getItem("token")) loadCart();
  }, []);

  // üçΩ Fetch menu
  const loadMenu = async () => {
    try {
      const data = await API("/items");
      setItems(data || []);
      setFilteredItems(data || []);
    } catch (err) {
      console.error("‚ùå Failed to load items:", err);
    }
  };

  // üõí Fetch cart
  const loadCart = async () => {
    try {
      const res = await API("/cart");
      const list = res.items || res.cart || [];
      setCart(
        list.map((it) => ({
          id: it.cart_id || it.id || it.item_id,
          item_id: it.item_id,
          name: it.name,
          price: Number(it.price),
          quantity: Number(it.quantity),
        }))
      );
      setShowCart(list.length > 0);
    } catch (err) {
      console.error("‚ùå Failed to load cart:", err);
    }
  };

  // ‚ûï Add to cart
  const addToCart = async (item) => {
    if (!localStorage.getItem("token")) {
      setShowAuth(true);
      return;
    }

    setCart((prev) => {
      const exists = prev.find((c) => c.item_id === item.id);
      if (exists) {
        return prev.map((c) =>
          c.item_id === item.id ? { ...c, quantity: c.quantity + 1 } : c
        );
      } else {
        return [...prev, { ...item, item_id: item.id, quantity: 1 }];
      }
    });
    setShowCart(true);

    try {
      await API("/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: item.id, quantity: 1 }),
      });
    } catch (err) {
      console.error("‚ùå Add to cart failed:", err);
    }
  };

  // ‚ûï Increase qty
  const increaseQty = async (item) => {
    setCart((prev) =>
      prev.map((c) =>
        c.item_id === item.item_id ? { ...c, quantity: c.quantity + 1 } : c
      )
    );

    try {
      await API("/cart/add", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: item.item_id, quantity: 1 }),
      });
    } catch (err) {
      console.error("‚ùå Increase failed:", err);
    }
  };

  // ‚ûñ Decrease qty or remove if 0
  const decreaseQty = async (item) => {
    if (item.quantity <= 1) {
      setCart((prev) => prev.filter((c) => c.item_id !== item.item_id));
      try {
        await API("/cart/remove", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ item_id: item.item_id }),
        });
      } catch (err) {
        console.error("‚ùå Remove failed:", err);
      }
    } else {
      setCart((prev) =>
        prev.map((c) =>
          c.item_id === item.item_id ? { ...c, quantity: c.quantity - 1 } : c
        )
      );

      try {
        await API("/cart/add", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ item_id: item.item_id, quantity: -1 }),
        });
      } catch (err) {
        console.error("‚ùå Decrease failed:", err);
      }
    }
  };

  // üí≥ Checkout
  const checkout = async () => {
    try {
      await API("/cart/checkout", { method: "POST" });
      alert("‚úÖ Order placed successfully!");
      setCart([]);
      setShowCart(false);
    } catch (err) {
      console.error("‚ùå Checkout failed:", err);
    }
  };

  // üîç Filter menu
  useEffect(() => {
    let list = [...items];
    if (selectedCategory !== "All") {
      list = list.filter((i) => i.category === selectedCategory);
    }
    if (search.trim()) {
      list = list.filter((i) =>
        i.name.toLowerCase().includes(search.toLowerCase())
      );
    }
    setFilteredItems(list);
  }, [search, selectedCategory, items]);

  // üí∞ Total
  const total = cart.reduce(
    (sum, i) => sum + i.price * i.quantity,
    0
  );

  return (
    <div className={`split-layout ${showCart ? "" : "full-width"}`}>
      {/* LEFT: MENU */}
      <div className="menu-section">
        <h2>Menu</h2>

        <input
          type="text"
          placeholder="Search for dishes..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-bar"
        />

        <div className="categories">
          {categories.map((cat) => (
            <button
              key={cat}
              className={`cat-btn ${
                selectedCategory === cat ? "active" : ""
              }`}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </button>
          ))}
        </div>

        <div className="grid">
          {filteredItems.map((it) => (
            <div key={it.id} className="card">
              <h4>{it.name}</h4>
              <p>{it.description}</p>
              <div>‚Çπ{it.price}</div>
              <button className="button" onClick={() => addToCart(it)}>
                Add to Cart
              </button>
            </div>
          ))}
          {filteredItems.length === 0 && <p>No items found.</p>}
        </div>
      </div>

      {/* RIGHT: CART */}
      {showCart && (
        <div className="cart-section">
          <h2>Your Cart</h2>
          {cart.length > 0 ? (
            <>
              <table className="cart-table">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Quantity</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((it) => (
                    <tr key={it.item_id}>
                      <td>{it.name}</td>
                      <td>
                        <div className="qty-controls">
                          <button onClick={() => decreaseQty(it)}>-</button>
                          <span>{it.quantity}</span>
                          <button onClick={() => increaseQty(it)}>+</button>
                        </div>
                      </td>
                      <td>‚Çπ{(it.price * it.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="cart-total">
                <h3>Total: ‚Çπ{total.toFixed(2)}</h3>
                <button className="button" onClick={checkout}>
                  Checkout
                </button>
              </div>
            </>
          ) : (
            <p>Your cart is empty.</p>
          )}
        </div>
      )}

      {showAuth && (
        <AuthModal onClose={() => setShowAuth(false)} onLogin={onLogin} />
      )}
    </div>
  );
}
