import React, { useState } from 'react';
import API from '../api';
import Cropper from 'react-easy-crop';
import './AuthModal.css';

export default function AuthModal({ onClose, onLogin }) {
  const [mode, setMode] = useState('login');
  const [form, setForm] = useState({
    name: '',
    age: '',
    address: '',
    mobile: '',
    email: '',
    password: '',
    photo: null,
  });
  const [loading, setLoading] = useState(false);
  const [imageSrc, setImageSrc] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);

  const change = (k, v) => setForm({ ...form, [k]: v });

  const onCropComplete = (_, croppedPixels) => {
    setCroppedAreaPixels(croppedPixels);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImageSrc(reader.result);
      reader.readAsDataURL(file);
    }
  };

  // Convert cropped area into file
  const getCroppedImageBlob = async () => {
    const image = new Image();
    image.src = imageSrc;
    await new Promise((r) => (image.onload = r));
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const { x, y, width, height } = croppedAreaPixels;
    canvas.width = width;
    canvas.height = height;
    ctx.drawImage(image, x, y, width, height, 0, 0, width, height);
    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        blob.name = 'cropped.jpg';
        resolve(blob);
      }, 'image/jpeg');
    });
  };

  const submit = async () => {
    setLoading(true);
    try {
      if (mode === 'login') {
        const res = await API('/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            email: form.email,
            password: form.password,
          }),
        });
        if (res.token) {
          onLogin(res.token, res.user);
          onClose();
        } else {
          alert(res.error || 'Login failed');
        }
      } else {
        const fd = new FormData();
        for (const key in form) {
          if (key !== 'photo') fd.append(key, form[key]);
        }
        if (imageSrc && croppedAreaPixels) {
          const blob = await getCroppedImageBlob();
          fd.append('photo', blob);
        }

        const res = await fetch(`${process.env.REACT_APP_API_URL}/auth/signup`, {
          method: 'POST',
          body: fd,
        }).then((r) => r.json());

        if (res.token) {
          onLogin(res.token, res.user);
          onClose();
        } else {
          alert(res.error || 'Signup failed');
        }
      }
    } catch (e) {
      console.error(e);
      alert('Error connecting to server.');
    }
    setLoading(false);
  };

  return (
    <div className="auth-overlay">
      <div className="auth-card">
        <h2 className="auth-title">
          {mode === 'login' ? 'Welcome Back!' : 'Create Account'}
        </h2>

        {mode === 'signup' && (
          <div className="photo-upload-section">
            <div className="photo-preview">
              {previewUrl ? (
                <img src={previewUrl} alt="Preview" />
              ) : (
                <div className="empty-photo">+</div>
              )}
            </div>
            <label className="upload-btn">
              Upload Photo
              <input type="file" accept="image/*" onChange={handleFileChange} />
            </label>

            {/* {imageSrc && (
              <div className="cropper-container">
                <Cropper
                  image={imageSrc}
                  crop={crop}
                  zoom={zoom}
                  aspect={1}
                  cropShape="round"
                  showGrid={false}
                  onCropChange={setCrop}
                  onCropComplete={onCropComplete}
                  onZoomChange={setZoom}
                />
                <button
                  className="button small"
                  onClick={() => {
                    const reader = new FileReader();
                    getCroppedImageBlob().then((blob) => {
                      const url = URL.createObjectURL(blob);
                      setPreviewUrl(url);
                      setImageSrc(null);
                    });
                  }}
                >
                  Confirm Crop
                </button>
              </div>
            )} */}
            {imageSrc && (
              <div className="cropper-container" style={{ position: 'relative' }}>
                <Cropper
                  image={imageSrc}
                  crop={crop}
                  zoom={zoom}
                  aspect={1}
                  cropShape="round"
                  showGrid={false}
                  onCropChange={setCrop}
                  onCropComplete={onCropComplete}
                  onZoomChange={setZoom}
                />
                <button
                  className="small"
                  style={{
                    position: 'absolute',
                    bottom: '8px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    zIndex: 3000,
                  }}
                  onClick={() => {
                    getCroppedImageBlob().then((blob) => {
                      const url = URL.createObjectURL(blob);
                      setPreviewUrl(url);
                      setImageSrc(null);
                    });
                  }}
                >
                  Confirm Crop
                </button>
              </div>
            )}

          </div>
        )}

        {mode === 'signup' && (
          <>
            <input
              placeholder="Full Name"
              value={form.name}
              onChange={(e) => change('name', e.target.value)}
            />
            <input
              placeholder="Age"
              value={form.age}
              onChange={(e) => change('age', e.target.value)}
            />
            <input
              placeholder="Address"
              value={form.address}
              onChange={(e) => change('address', e.target.value)}
            />
            <input
              placeholder="Mobile"
              value={form.mobile}
              onChange={(e) => change('mobile', e.target.value)}
            />
          </>
        )}

        <input
          placeholder="Email"
          value={form.email}
          onChange={(e) => change('email', e.target.value)}
        />
        <input
          placeholder="Password"
          type="password"
          value={form.password}
          onChange={(e) => change('password', e.target.value)}
        />

        <button className="auth-btn" onClick={submit} disabled={loading}>
          {loading ? 'Please wait...' : mode === 'login' ? 'Login' : 'Sign Up'}
        </button>

        <div className="auth-switch">
          {mode === 'login' ? (
            <p>
              New User? <span onClick={() => setMode('signup')}>Sign Up</span>
            </p>
          ) : (
            <p>
              Already have an account?{' '}
              <span onClick={() => setMode('login')}>Login</span>
            </p>
          )}
        </div>

        <button className="auth-close" onClick={onClose}>
          ✕
        </button>
      </div>
    </div>
  );
}
