import React, { useEffect, useState } from 'react';
import API from '../api';
import './Profile.css';

export default function Profile() {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    loadProfile();
  }, []);

  const loadProfile = async () => {
    const res = await API('/auth/profile');
    if (res.user) {
      setUser(res.user);
      setOrders(res.orders);
    }
  };
  <div className="profile-photo-section">
  <h3>Profile Photo</h3>
  {user.photo ? (
    <img
      src={`${process.env.REACT_APP_API_URL.replace('/api', '')}${user.photo}`}
      alt="Profile"
      className="profile-photo"
    />
  ) : (
    <p>No photo uploaded.</p>
  )}
  <label className="upload-btn">
    Change Photo
    <input
      type="file"
      accept="image/*"
      onChange={async (e) => {
        const fd = new FormData();
        fd.append('photo', e.target.files[0]);
        const res = await fetch(`${process.env.REACT_APP_API_URL}/auth/upload-photo`, {
          method: 'POST',
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          body: fd
        }).then((r) => r.json());
        if (res.photo) {
          setUser({ ...user, photo: res.photo });
          alert('✅ Profile photo updated!');
        }
      }}
    />
  </label>
</div>


  if (!user) return <p>Loading profile...</p>;

  return (
    <div className="profile-page">
      <h2>My Profile</h2>

      <div className="profile-card">
        <h3>Personal Details</h3>
        <p><b>Name:</b> {user.name}</p>
        <p><b>Age:</b> {user.age}</p>
        <p><b>Mobile:</b> {user.mobile}</p>
        <p><b>Email:</b> {user.email}</p>
        <p><b>Address:</b> {user.address}</p>
        <p><b>Member Since:</b> {new Date(user.created_at).toLocaleDateString()}</p>
      </div>

      <div className="order-history">
        <h3>Order History</h3>
        {orders.length === 0 ? (
          <p>No orders yet.</p>
        ) : (
          orders.map((order) => (
            <div key={order.id} className="order-card">
              <div className="order-header">
                <span>Order #{order.id}</span>
                <span>{new Date(order.created_at).toLocaleString()}</span>
              </div>
              <div className="order-items">
                {order.items.map((it, idx) => (
                  <div key={idx} className="order-item">
                    <span>{it.name}</span>
                    <span>
                      ₹{it.price} × {it.quantity}
                    </span>
                  </div>
                ))}
              </div>
              <div className="order-total">
                <b>Total: ₹{order.total}</b> <span>({order.status})</span>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
