import React, { useState } from 'react';
import API from '../api';

export default function AuthModal({ onClose, onLogin }){
  const [mode, setMode] = useState('login');
  const [form, setForm] = useState({name:'', age:'', address:'', mobile:'', email:'', password:''});
  const change = (k,v)=>setForm({...form,[k]:v});

  const submit = async () => {
    if (mode==='login'){
      const res = await API('/auth/login', { method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify({ email: form.email, password: form.password })});
      if (res.token){ onLogin(res.token, res.user); onClose(); } else alert(res.error || 'login failed');
    } else {
      const res = await API('/auth/signup', { method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify(form)});
      if (res.token){ onLogin(res.token, res.user); onClose(); } else alert(res.error || 'signup failed');
    }
  };

  return (
    <div style={{position:'fixed',left:0,top:0,right:0,bottom:0,display:'flex',alignItems:'center',justifyContent:'center',background:'rgba(0,0,0,0.4)'}}>
      <div style={{width:400, background:'white', padding:20, borderRadius:8}}>
        <h3>{mode==='login' ? 'Login' : 'Signup'}</h3>
        {mode==='signup' && <>
          <input placeholder="Name" value={form.name} onChange={e=>change('name', e.target.value)} /><br/>
          <input placeholder="Age" value={form.age} onChange={e=>change('age', e.target.value)} /><br/>
          <input placeholder="Address" value={form.address} onChange={e=>change('address', e.target.value)} /><br/>
          <input placeholder="Mobile" value={form.mobile} onChange={e=>change('mobile', e.target.value)} /><br/>
        </>}
        <input placeholder="Email" value={form.email} onChange={e=>change('email', e.target.value)} /><br/>
        <input placeholder="Password" type="password" value={form.password} onChange={e=>change('password', e.target.value)} /><br/>
        <div style={{marginTop:10}}>
          <button className="button" onClick={submit}>{mode==='login' ? 'Login' : 'Signup'}</button>{' '}
          <button onClick={()=>setMode(mode==='login'?'signup':'login')}>{mode==='login' ? 'Switch to Signup' : 'Switch to Login'}</button>{' '}
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  );
}
